ALTER TABLE `devis` ADD `date_validite` DATE NULL DEFAULT NULL AFTER `client_id`, ADD `moyen_reglement` ENUM('cheque') NOT NULL DEFAULT 'cheque' AFTER `date_validite`, ADD `delai_reglements` ENUM('commande') NOT NULL DEFAULT 'commande' AFTER `moyen_reglement`, ADD `accompte` INT NULL AFTER `delai_reglements`, ADD `created` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP AFTER `accompte`, ADD `modified` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP AFTER `created`;